DROP TABLE IF EXISTS `gz_order`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `gz_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `order_sn` varchar(50) DEFAULT '' COMMENT '订单编号',
  `order_type` enum('usercard','package','course') DEFAULT 'course' COMMENT '订单类型',
  `course_id` int(11) DEFAULT '0' COMMENT '订单类型对应的产品id',
  `package_id` int(11) DEFAULT '0' COMMENT '套餐id',
  `usercard_id` int(11) DEFAULT '0' COMMENT '会员卡id',
  `cardtype` int(3) DEFAULT '0' COMMENT '会员卡类型',
  `card_name` varchar(255) DEFAULT '' COMMENT '会员卡类型',
  `package_name` varchar(255) DEFAULT '' COMMENT '套餐名称',
  `price` decimal(10,2) DEFAULT '0.00',
  `pay_price` decimal(10,2) DEFAULT '0.00' COMMENT '实际支付',
  `create_time` int(11) DEFAULT '1483200000',
  `update_time` int(11) DEFAULT '1483200000',
  `expire_time` int(11) DEFAULT '1483200000' COMMENT '到期时间',
  `course_name` varchar(255) DEFAULT '' COMMENT '产品名称',
  `course_banner` varchar(100) DEFAULT '' COMMENT '产品图片',
  `uid` int(11) DEFAULT '0',
  `pay_status` tinyint(1) DEFAULT '0' COMMENT '0 未付款 1已付款',
  `pay_type` tinyint(1) DEFAULT '0' COMMENT '0无 1 微信 2支付宝',
  `closed` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0未删除 1 删除',
  `mobile` char(11) DEFAULT '' COMMENT '手机号',
  `is_import` tinyint(1) DEFAULT '0' COMMENT '是否导入',
  `qq` varchar(20) DEFAULT '' COMMENT 'QQ号码',
  `realname` varchar(50) DEFAULT '' COMMENT '真实姓名',
  `remark` varchar(255) DEFAULT '',
  `source` varchar(255) DEFAULT '' COMMENT '来源',
  `coupon_id` int(11) DEFAULT NULL,
  `coupon_code` varchar(10) DEFAULT NULL,
  `old_price` decimal(10,2) DEFAULT '0.00' COMMENT '优惠前价格',
  `is_activity` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否为活动页面',
  `integral` int(11) NOT NULL DEFAULT '0' COMMENT '使用积分数',
  `cash_deducted` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '积分抵扣现金',
